Bash(dotnet run --project src/GDShrapt.Reader.Tests -- -c "
using GDShrapt.Reader;
using System;
using System.Linq;

var code = @""
func test\(\):
    break
"";

var reader = new GDScriptReader\(\);
var tree = reader.ParseFileContent\(code\);

// Print the tree structure
void PrintTree\(GDNode node, string indent = """"\)
{
    Console.WriteLine\(indent + node.GetType\(\).Name\);
    foreach \(var child in node.Nodes\)
    {
        PrintTree\(child, indent + ""  ""\);
    }
}

PrintTree\(tree\);
")

